j = i + L;
if (L == 0) {
    entry = 0;
} else {
    let optimalCost = Number.MAX_VALUE; // optimal cost of the subproblem defined by i, j
    for (k = i; k < j; k++) {
        // kOpt is the optimal cost if we had split at k
        kOpt = T(i, k) // optimal cost of left subproblem
            + T(k + 1, j)  // optimal cost of right subproblem
            + dims[i] * dims[k + 1] * dims[j + 1]; // cost to combine the two subproblems
        if (kOpt < optimalCost) {
            optimalCost = kOpt;
        }
    }
    entry = optimalCost; // write the optimal cost into entry i,j
}
